package com.example.dodo.myapplication;import android.content.DialogInterface;import android.content.Intent;import android.support.v7.app.AlertDialog;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.TextView;import android.widget.Toast;import dao.StickNoteDao;import model.StickNote;public class AcaoStickNote extends AppCompatActivity {    StickNoteDao stickNoteDao;    int idStickNote = 0;    boolean back = false;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_acao_stick_note);        stickNoteDao = new StickNoteDao(this);        TextView stickNoteTitulo = (TextView)findViewById(R.id.textViewTitulo);        TextView stickNoteNota = (TextView)findViewById(R.id.textViewNota);        Intent it = getIntent();        Bundle b = it.getExtras();        if(b!=null)        {            idStickNote =  Integer.parseInt(b.get("idStickNote").toString());            StickNote stickNoteModel = stickNoteDao.getStickNoteById(idStickNote);            stickNoteNota.setText(stickNoteModel.getNota());            stickNoteTitulo.setText(stickNoteModel.getTitulo());        }    }    public void deletar(View v){        AlertDialog.Builder dialogo = new AlertDialog.Builder(this);        dialogo.setTitle("Confirmacao de exclusao");        dialogo.setMessage("Esse contato sera excluido");        AlertDialog.Builder ok = dialogo.setPositiveButton("OK", new DialogInterface.OnClickListener() {            @Override            public void onClick(DialogInterface dialogInterface, int i) {                if (stickNoteDao.deleteStickNote(idStickNote)) {                    back = true;                    Toast.makeText(AcaoStickNote.this, "StickNote deletado", Toast.LENGTH_SHORT).show();                    finish();                }            }        });        dialogo.setNegativeButton("Cancelar", null);        dialogo.show();    }    public void concluir(View v){        AlertDialog.Builder dialogo = new AlertDialog.Builder(this);        dialogo.setTitle("Marcar conluído");        dialogo.setMessage("StickNote será concluído");        AlertDialog.Builder ok = dialogo.setPositiveButton("OK", new DialogInterface.OnClickListener() {            @Override            public void onClick(DialogInterface dialogInterface, int i) {                StickNote st = stickNoteDao.getStickNoteById(idStickNote);                st.setIndConcluido(1);                if (stickNoteDao.saveStickNote(st) > 0) {                    back = true;                    Toast.makeText(AcaoStickNote.this, "StickNote concluído", Toast.LENGTH_SHORT).show();                    finish();                }            }        });        dialogo.setNegativeButton("Cancelar", null);        dialogo.show();    }}