package com.example.dodo.myapplication;import android.content.Intent;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.TextView;public class AvisoCadastroOk extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_aviso_cadastro_ok);        TextView congrats = (TextView)findViewById(R.id.textViewCongratulations);        Intent it = getIntent();        Bundle b = it.getExtras();        if(b!=null)        {            String nome =(String) b.get("usuario");            congrats.setText("Parab√©ns, " + nome +". Seu cadastro foi realizado com sucesso!");        }    }    public void voltarLogar(View view){        Intent it = new Intent(this, MainActivity.class);        startActivity(it);        setContentView(R.layout.activity_main);        finish();    }}