package model;/** * Created by dodo on 20/05/16. */public class Usuario {    private int id;    private String nome;    private String login;    private String senha;    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getNome() {        return nome;    }    public void setNome(String nome) {        this.nome = nome;    }    public void setSenha(String senha) {        this.senha = senha;    }    public String getSenha() {        return senha;    }    public String getLogin() {        return login;    }    public void setLogin(String login) {        this.login = login;    }}